using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.SceneManagement;  public class MainMenu : MonoBehaviour {  	public void PlayGame() {  		SceneManager.LoadScene (SceneManager.GetActiveScene ().buildIndex + 1);   	}  	public void QuitGame() { 		//Doesn't even work in webgl builds, quit button will direct to credits screen instead 		Debug.Log ("quit working"); 		Application.Quit ();  	} 	//directs back into menu 	public void Replay(){  		SceneManager.LoadScene ("Menu");  	} 	//goes to credits scene 	public void Credits(){  		SceneManager.LoadScene ("Credits");  	} 	//loads instructions level 	public void Tutorial(){  		SceneManager.LoadScene ("Tutorial");  	} } 